'use strict';


module.exports = function createJSONSchemaTypeArray(type, bsonType, useBsonType, isRequired) {
  if (useBsonType) {
    if (isRequired) {
      return { bsonType };
    }
    return { bsonType: [bsonType, 'null'] };
  } else {
    if (isRequired) {
      return { type };
    }
    return { type: [type, 'null'] };
  }
};
